Template.lyricstemplate.preview = function(){
  var lines, i$, ref$, len$, line;
  lines = [];
  for (i$ = 0, len$ = (ref$ = this.text.split('\n')).length; i$ < len$; ++i$) {
    line = ref$[i$];
    line = line.trim();
    if (line[0] === '[') {
      continue;
    }
    lines.push(line);
  }
  return lines.join(' ').substring(0, 40);
};
